if ( ! function_exists( 'theme_demo_import_ninja_forms_v3' ) && !get_option('theme_demo_import_ninja_forms') ) :
    function theme_demo_import_ninja_forms_v3( $forms_data = array() ) {

        update_option( 'theme_demo_import_ninja_forms', 'started' );
        // Loop through data and add to DB as needed
        foreach ( $forms_data as $option_alias => $option_data ) {

            // Generate the 'option_name' for the row
            $theme_name  = wp_get_theme();
            $option_name = sanitize_key( $theme_name->get( 'Name' ) . '_' . $option_alias );

            // Check for a previous import
            if ( ! get_option( $option_name ) ) {

                // NF_Admin_AllFormsTable::delete_item( absint( $option_data['id'] ) ); // this seems dangerous, deleting forms by ID!
                // $import = Ninja_Forms()->form()->import_form( $option_data['content'], absint( $option_data['id'] ) );
                $import = Ninja_Forms()->form()->import_form( $option_data['content'], true, absint( $option_data['id'] ) );

                if ( $import ) {
                    $option_data['id'] = $import;
                    update_option( $option_name, maybe_unserialize( $option_data, true ) ); // backup and log import in DB
                }

            }
        }
        delete_option('theme_demo_import_ninja_forms');
    }
endif;

if (!function_exists('theme_demo_import_ninja_forms') && !get_option('theme_demo_import_ninja_forms')) :
    function theme_demo_import_ninja_forms( $forms_data = array() ) {
        update_option( 'theme_demo_import_ninja_forms', 'started' );
        // Loop through data and add to DB as needed
        foreach ($forms_data as $option_alias => $option_data) {

            // Generate the 'option_name' for the row
            $theme_name  = wp_get_theme();
            $option_name = sanitize_key( $theme_name->get('Name').'_'.$option_alias);

            // Check for a previous import
            if( !get_option( $option_name ) ) {
                if (function_exists('ninja_forms_import_form')) {
                    ninja_forms_import_form( $option_data );                                // add ninja forms data
                    update_option( $option_name, maybe_unserialize( $option_data, true ) ); // backup and log import in DB
                }
            }
        }
        delete_option('theme_demo_import_ninja_forms');
    }
endif;


